import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import $ from 'jquery';



function TopMenu(props) {

    const [buttonSelected, setButton] = useState('userCreateButton');
    const itemClick=(eve)=>{
        const buttonId = '#'+eve.target.id;
        const buttonClass = eve.target.className.split(' '); // buttons className in a array
        console.log(buttonClass);
        if (buttonClass[0]!='active'){

            $(buttonId).className('active item'); // make the new button active
            console.log( $(buttonId) );
            // $(buttonSelected).className('item'); // remove the old active button

            // $(buttonSelected).setButton(buttonId); // add active button to state
        }
    }
    return (
        <div className="ui tabular menu" onClick={itemClick}>
            <Link id='userCreateButton' to='/UserCreate' className="active item">
                Playas & Scores
                </Link>
            <Link id='simonButton' to='/games/simon' className="item">
                Simon
            </Link>
            <Link id='tictactoeButton' to='/games/TicTacToe' className="item">
                TicTacToe
            </Link>

        </div>
    );
};

export default TopMenu;

