import React from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';

import TopMenu from './TopMenu';
import Simon from '../games/Simon';
import UserCreate from './UserCreate';
import TicTacToe from '../games/TicTacToe';

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            p1Name: "Juan",
            p2Name: "Marc",
            p1Score: 0,
            p2Score: 0,
            p1Color: "red",
            p2Color: "blue"
        }
        this.sendInfoToApp = this.sendInfoToApp.bind(this);
        this.sendScoreToApp = this.sendScoreToApp.bind(this);


    }

    sendScoreToApp(info) {
        console.log(info);
        if (info === this.state.p1Name) {
            this.setState({ p1Score: this.state.p1Score + 1 })
        }
        else if (info === this.state.p2Name) {
            this.setState({ p2Score: this.state.p2Score + 1 })
        }
    }

    sendInfoToApp(info) {
        console.log(info.p1Name)
        this.setState({
            p1Name: info.p1Name,
            p2Name: info.p2Name,
            p1Color: info.p1Color,
            p2Color: info.p2Color

        })
    }// fun={[this.state, this.sendScoreToApp]}

    render() {
        return (

            <div>
                {/* <UserCreate fun={this.sendInfoToApp} /> */}
                <BrowserRouter>
                    <TopMenu />
                    <Route path='/games/simon' exact component={Simon} fun={[this.state, this.sendScoreToApp]} className="item" />
                    <Route path='/UserCreate' fun={this.sendInfoToApp} exact component={UserCreate} className="item" />
                    <Route path='/games/TicTacToe' component={{state: this.state, sendCoreToApp:this.sendScoreToApp, TicTacToe: TicTacToe}} exact  className="item" />
                </BrowserRouter>

            </div>

        );

    }
};

export default App;
