import React from 'react';
import { render } from 'react-dom';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import SplitPane from 'react-split-pane';

import Simon from '../games/Simon';

const styles = {
  background: '#000',
  width: '2px',
  cursor: 'col-resize',
  margin: '0 5px',
  height: '100%',
};

const SideMenu = () => (
  <Router>
    <SplitPane
      split="vertical"
      minSize={100}
      defaultSize={100}
      resizerStyle={styles}
    >
      <menu>
        <div><Link to="/games/simon">Simon</Link></div>

      </menu>
      <div>
        <Route exact path="/games/simon" component={Simon} />

      </div>
    </SplitPane>
  </Router>
);

render(<SideMenu />, document.body);




