import React from 'react';
import { BrowserRouter, Route, Switch, Link } from 'react-router-dom';




function TopMenu(props) {
    return (

        <div className="ui tabular menu">
            <Link to='/UserCreate' className="item active">
                Playas & Scores
                </Link>
            <Link to='/games/simon' className="item">
                Simon
            </Link>
            <Link to='/games/TicTacToe' className="item">
                TicTacToe
            </Link>

        </div>
    );
};

export default TopMenu;






