import React from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';

import SideMenu from './SideMenu';
import Simon from '../games/Simon';
import UserCreate from './UserCreate';
import TicTacToe from '../games/TicTacToe';

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            p1Name: "",
            p2Name: "",
            p1Email: "",
            p2Email: ""
        }
        this.sendInfoToApp = this.sendInfoToApp.bind(this);

    }
    sendInfoToApp(info) {
        console.log(info);    
    }

    render() {
        return (

            <div>
                {/* <UserCreate fun={this.sendInfoToApp} /> */}
                <BrowserRouter>
                    <SideMenu />
                    <Route path='/games/simon' exact component={Simon} className="item" />
                    <Route path='/userCreate'fun={this.sendInfoToApp} exact component={UserCreate} className="item" />
                    <Route path='/games/TicTacToe'fun={this.sendInfoToApp} exact component={TicTacToe} className="item" />
                 </BrowserRouter>

            </div>

        );

    }
};

export default App;
